import "./App.css";
import { useState, useEffect } from "react";
import Star from "./Star";
import Loader from "./Loader";
import MovieDetails from "./movieDetails"; // Assuming MovieDetails component is imported correctly

function App() {
  const [query, setQuery] = useState("");
  const [movieData, setMovieData] = useState(null);
  const [favorites, setFavorites] = useState(() => {
    const savedFavorites = JSON.parse(localStorage.getItem("favorites"));
    return savedFavorites ? savedFavorites : [];
  });
  const [loading, setLoading] = useState(false);
  const [selectedMovie, setSelectedMovie] = useState(null); // State to track selected movie

  useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem("favorites"));
    if (savedFavorites) {
      setFavorites(savedFavorites);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("favorites", JSON.stringify(favorites));
  }, [favorites]);

  const fetchMovie = async (movieName) => {
    setLoading(true);
    try {
      const URL = `http://www.omdbapi.com/?t=${movieName}&apikey=e45311a3`;
      const response = await fetch(URL);
      const finalData = await response.json();

      if (finalData.Response === "False") {
        setMovieData({
          Title: "No movie found",
        });
      } else {
        setMovieData(finalData);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    fetchMovie(query);
    setSelectedMovie(null); // Reset selected movie when performing new search
  };

  const handleMovieClick = (movie) => {
    setMovieData(null); // Clear movieData to avoid showing previous details
    setSelectedMovie(movie);
  };

  const handleFavoriteToggle = (movie) => {
    setFavorites((prevFavorites) => {
      if (prevFavorites.find((fav) => fav.imdbID === movie.imdbID)) {
        return prevFavorites.filter((fav) => fav.imdbID !== movie.imdbID);
      } else {
        return [...prevFavorites, movie];
      }
    });
  };

  return (
    <div className="app-container">
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for a movie"
        className="search-input"
      />
      <button onClick={handleSearch} className="search-button">
        Search
      </button>
      <div className="movies-container">
        {loading ? (
          <Loader />
        ) : (
          <div className="movies-list">
            {favorites.length > 0 &&
              favorites.map((movie) => (
                <div
                  key={movie.imdbID}
                  className="card"
                  onClick={() => handleMovieClick(movie)}
                >
                  <div className="star-container">
                    <Star
                      isFavorite={true}
                      onToggle={() => handleFavoriteToggle(movie)}
                    />
                  </div>
                  <h3>{movie.Title}</h3>
                  {movie.Poster && (
                    <img
                      src={movie.Poster}
                      alt={`${movie.Title} Poster`}
                      className="card-container"
                    />
                  )}
                </div>
              ))}
          </div>
        )}
      </div>
      {selectedMovie && (
        <MovieDetails
          movieData={selectedMovie}
          onClose={() => setSelectedMovie(null)} // Provide a way to close MovieDetails
        />
      )}
    </div>
  );
}

export default App;
