import React, { useState, useEffect } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  Link,
  useParams,
} from "react-router-dom";
import Star from "./Star";
import Loader from "./Loader";
import MovieDetails from "./movieDetails";

const App = () => {
  const [query, setQuery] = useState("");
  const [movieData, setmovieData] = useState(null);
  const [favorites, setFavorites] = useState(() => {
    const savedFavorites = JSON.parse(localStorage.getItem("favorites"));
    return savedFavorites ? savedFavorites : [];
  });
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem("favorites"));
    if (savedFavorites) {
      setFavorites(savedFavorites);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("favorites", JSON.stringify(favorites));
  }, [favorites]);

  const fetchMovie = async (movieName) => {
    setLoading(true);
    try {
      const URL = `http://www.omdbapi.com/?t=${movieName}&apikey=e45311a3`;
      const response = await fetch(URL);
      const finalData = await response.json();

      if (finalData.Response === "False") {
        setMovieData({
          Title: "No movie found",
        });
      } else {
        setMovieData(finalData);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    fetchMovie(query);
  };

  const handleFavoriteToggle = (movie) => {
    const isFavorite = favorites.some((fav) => fav.imdbID === movie.imdbID);
    if (isFavorite) {
      const updatedFavorites = favorites.filter(
        (fav) => fav.imdbID !== movie.imdbID
      );
      setFavorites(updatedFavorites);
    } else {
      setFavorites([...favorites, movie]);
    }
  };

  return (
    <Router>
      <div className="app-container">
        <nav>
          <Link to="/">Home</Link> <Link to="/favorites">View Favorites</Link>
        </nav>

        <Routes>
          <Route
            path="/"
            element={
              <Home
                query={query}
                setQuery={setQuery}
                handleSearch={handleSearch}
                loading={loading}
                setLoading={setLoading}
                movieData={movieData}
                favorites={favorites}
                handleFavoriteToggle={handleFavoriteToggle}
              />
            }
          />
          <Route
            path="/movie/:title"
            element={
              <MovieDetailsPage
                favorites={favorites}
                handleFavoriteToggle={handleFavoriteToggle}
              />
            }
          />
          <Route
            path="/favorites"
            element={
              <Favorites
                favorites={favorites}
                handleFavoriteToggle={handleFavoriteToggle}
              />
            }
          />
        </Routes>
      </div>
    </Router>
  );
};

const Home = ({
  query,
  setQuery,
  handleSearch,
  loading,
  setLoading,
  movieData,
  favorites,
  handleFavoriteToggle,
}) => {
  const handleMovieCardClick = (movie) => {
    setMovieData(movie);
  };

  return (
    <>
      <input
        type="text"
        value={query}
        onChange={(e) => setQuery(e.target.value)}
        placeholder="Search for a movie"
        className="search-input"
      />
      <button onClick={handleSearch} className="search-button">
        Search
      </button>

      {loading ? (
        <Loader />
      ) : (
        <SearchResults
          movieData={movieData}
          favorites={favorites}
          handleFavoriteToggle={handleFavoriteToggle}
          handleMovieCardClick={handleMovieCardClick}
        />
      )}
    </>
  );
};

const SearchResults = ({
  movieData,
  favorites,
  handleFavoriteToggle,
  handleMovieCardClick,
}) => {
  return (
    <div className="search-results">
      {movieData && (
        <div className="card" onClick={() => handleMovieCardClick(movieData)}>
          <div className="star-container">
            <Star
              isFavorite={favorites.some(
                (fav) => fav.imdbID === movieData.imdbID
              )}
              onToggle={() => handleFavoriteToggle(movieData)}
            />
          </div>
          <h1>{movieData.Title}</h1>
          <p>Release Date: {movieData.Released}</p>
          {movieData.Poster ? (
            <img src={movieData.Poster} alt={`${movieData.Title} Poster`} />
          ) : (
            <p>No poster available</p>
          )}
        </div>
      )}
    </div>
  );
};

const MovieDetailsPage = ({ favorites, handleFavoriteToggle }) => {
  let { title } = useParams();
  const [movieData, setMovieData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      setLoading(true);
      try {
        const URL = `http://www.omdbapi.com/?t=${title}&apikey=e45311a3`;
        const response = await fetch(URL);
        const finalData = await response.json();

        if (finalData.Response === "False") {
          setMovieData({
            Title: "No movie found",
          });
        } else {
          setMovieData(finalData);
        }
      } catch (err) {
        console.log(err);
      } finally {
        setLoading(false);
      }
    };

    fetchMovieDetails();
  }, [title]);

  const handleMovieDetailsFavoriteToggle = () => {
    handleFavoriteToggle(movieData);
  };

  return (
    <div className="movie-details">
      {loading ? (
        <Loader />
      ) : movieData ? (
        <>
          <div className="star-container">
            <Star
              isFavorite={favorites.some(
                (fav) => fav.imdbID === movieData.imdbID
              )}
              onToggle={handleMovieDetailsFavoriteToggle}
            />
          </div>
          <h1>{movieData.Title}</h1>
          <p>Release Date: {movieData.Released}</p>
          {movieData.Poster ? (
            <img src={movieData.Poster} alt={`${movieData.Title} Poster`} />
          ) : (
            <p>No poster available</p>
          )}
        </>
      ) : (
        <p>No movie details available.</p>
      )}
    </div>
  );
};

const Favorites = ({ favorites, handleFavoriteToggle }) => {
  return (
    <div className="favorites-list">
      <h2>Favorites</h2>
      {favorites.length > 0 ? (
        favorites.map((movie) => (
          <div key={movie.imdbID} className="card">
            <div className="star-container">
              <Star
                isFavorite={true}
                onToggle={() => handleFavoriteToggle(movie)}
              />
            </div>
            <h3>{movie.Title}</h3>
            {movie.Poster && (
              <img
                src={movie.Poster}
                alt={`${movie.Title} Poster`}
                className="card-container"
              />
            )}
          </div>
        ))
      ) : (
        <p>No favorites added yet.</p>
      )}
    </div>
  );
};

export default App;
