import "./App.css";
import { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import Star from "./Star";
import Loader from "./Loader";
import MovieDetails from "./movieDetails";

function App() {
  const [query, setQuery] = useState("");
  const [movieData, setMovieData] = useState(null);
  const [favorites, setFavorites] = useState(() => {
    const savedFavorites = JSON.parse(localStorage.getItem("favorites"));
    return savedFavorites ? savedFavorites : [];
  });
  const [showFavorites, setShowFavorites] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const savedFavorites = JSON.parse(localStorage.getItem("favorites"));
    if (savedFavorites) {
      setFavorites(savedFavorites);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("favorites", JSON.stringify(favorites));
  }, [favorites]);

  const fetchMovie = async (movieName) => {
    setLoading(true);
    try {
      const URL = `http://www.omdbapi.com/?t=${movieName}&apikey=e45311a3`;
      const response = await fetch(URL);
      const finalData = await response.json();

      if (finalData.Response === "False") {
        setMovieData({
          Title: "No movie found",
        });
      } else {
        setMovieData(finalData);
      }
    } catch (err) {
      console.log(err);
    } finally {
      setLoading(false);
    }
  };

  const handleSearch = () => {
    fetchMovie(query);
    setShowFavorites(false);
  };

  const handleFavoriteToggle = (movie) => {
    setFavorites((prevFavorites) => {
      if (prevFavorites.find((fav) => fav.imdbID === movie.imdbID)) {
        return prevFavorites.filter((fav) => fav.imdbID !== movie.imdbID);
      } else {
        return [...prevFavorites, movie];
      }
    });
  };

  return (
    <Router>
      <div className="app-container">
        <Switch>
          <Route path="/" exact>
            <input
              type="text"
              value={query}
              onChange={(e) => setQuery(e.target.value)}
              placeholder="Search for a movie"
              className="search-input"
            />
            <button onClick={handleSearch} className="search-button">
              Search
            </button>
            <button
              onClick={() => setShowFavorites(true)}
              className="favorites-button"
            >
              View Favorites
            </button>
            {loading ? (
              <Loader />
            ) : showFavorites ? (
              <div className="favorites-list">
                <h2>Favorites</h2>
                {favorites.length > 0 ? (
                  favorites.map((movie) => (
                    <div
                      key={movie.imdbID}
                      className="card"
                      onClick={() => {
                        // Navigate to movie details page
                        setMovieData(movie);
                        window.location.href = `/movie/${encodeURIComponent(
                          movie.Title
                        )}`;
                      }}
                    >
                      <div className="star-container">
                        <Star
                          isFavorite={true}
                          onToggle={() => handleFavoriteToggle(movie)}
                        />
                      </div>
                      <h3>{movie.Title}</h3>
                      {movie.Poster && (
                        <img
                          src={movie.Poster}
                          alt={`${movie.Title} Poster`}
                          className="card-container"
                        />
                      )}
                    </div>
                  ))
                ) : (
                  <p>No favorites added yet.</p>
                )}
              </div>
            ) : (
              <div className="search-results">
                {movieData && (
                  <div className="card">
                    <div className="star-container">
                      <Star
                        isFavorite={
                          !!favorites.find(
                            (fav) => fav.imdbID === movieData.imdbID
                          )
                        }
                        onToggle={() => handleFavoriteToggle(movieData)}
                      />
                    </div>
                    <h1>{movieData.Title}</h1>
                    <p>Release Date: {movieData.Released}</p>

                    {movieData.Poster ? (
                      <img
                        src={movieData.Poster}
                        alt={`${movieData.Title} Poster`}
                      />
                    ) : (
                      <p>No poster available</p>
                    )}
                  </div>
                )}
              </div>
            )}
          </Route>
          <Route path="/movie/:title">
            <MovieDetails movieData={movieData} />
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
